const handleLogin = (e) => {
  e.preventDefault();

  const storedUser = JSON.parse(localStorage.getItem('registeredUser'));

  if (storedUser && storedUser.email === email && storedUser.password === password) {
    localStorage.setItem('user', JSON.stringify({ email }));
    navigate('/dashboard');
  } else {
    alert('Invalid credentials');
  }
};
