import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const StudentDashboard = () => {
  const [studentData, setStudentData] = useState([
    { id: 1, name: 'Math 101', description: 'Basic Mathematics' },
    { id: 2, name: 'Physics 101', description: 'Fundamentals of Physics' },
  ]);
  const navigate = useNavigate();

  const handleEdit = (id) => {
    // Logic for editing a student item (course/assignment)
    const updatedData = studentData.map((item) =>
      item.id === id ? { ...item, name: 'Updated Course Name' } : item
    );
    setStudentData(updatedData);
  };

  const handleDelete = (id) => {
    // Logic for deleting a student item
    const filteredData = studentData.filter((item) => item.id !== id);
    setStudentData(filteredData);
  };

  const goToAdminDashboard = () => {
    navigate('/admin'); // Navigates to the admin dashboard
  };

  return (
    <div className="student-dashboard">
      <h1>Welcome to the Student Dashboard</h1>
      <button onClick={goToAdminDashboard}>Go to Admin Dashboard</button>

      <h2>Student Courses</h2>
      <ul>
        {studentData.map((item) => (
          <li key={item.id}>
            <strong>{item.name}</strong>: {item.description}
            <button onClick={() => handleEdit(item.id)}>Edit</button>
            <button onClick={() => handleDelete(item.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StudentDashboard;
