import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import StudentDashboard from './pages/StudentDashboard'; // ✅ Corrected import
import Topbar from './components/Topbar';
import Sidebar from './components/Sidebar';
import PrivateRoute from './components/PrivateRoute';
import './App.css';

function App() {
  const user = JSON.parse(localStorage.getItem('user'));

  return (
    <Router>
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Student Dashboard */}
        <Route
          path="/dashboard"
          element={
            <PrivateRoute>
              {user?.role === 'student' ? (
                <div className="app">
                  <Topbar />
                  <div className="main">
                    <Sidebar />
                    <StudentDashboard role="student" />
                  </div>
                </div>
              ) : (
                <h2 style={{ padding: '2rem' }}>Access Denied</h2>
              )}
            </PrivateRoute>
          }
        />

        {/* Admin Dashboard */}
        <Route
          path="/admin-dashboard"
          element={
            <PrivateRoute>
              {user?.role === 'admin' ? (
                <div className="app">
                  <Topbar />
                  <div className="main">
                    <Sidebar />
                    <StudentDashboard role="admin" />
                  </div>
                </div>
              ) : (
                <h2 style={{ padding: '2rem' }}>Access Denied</h2>
              )}
            </PrivateRoute>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
