import React, { useState } from 'react';
import './Dashboard.css';
import PrivateRoute from './components/PrivateRoute';

const Dashboard = () => {
  const [results, setResults] = useState([
    { id: 1, name: 'John Doe', subject: 'Math', score: 85 },
    { id: 2, name: 'Jane Smith', subject: 'Science', score: 92 },
  ]);

  const [newResult, setNewResult] = useState({ name: '', subject: '', score: '' });

  const handleAddResult = (e) => {
    e.preventDefault();
    const id = results.length ? results[results.length - 1].id + 1 : 1;
    setResults([...results, { ...newResult, id, score: parseInt(newResult.score) }]);
    setNewResult({ name: '', subject: '', score: '' });
  };

  const handleDelete = (id) => {
    setResults(results.filter(r => r.id !== id));
  };

  return (
    <div className="dashboard">
      <h2>Student Results</h2>

      <form className="add-form" onSubmit={handleAddResult}>
        <input
          type="text"
          placeholder="Name"
          value={newResult.name}
          onChange={(e) => setNewResult({ ...newResult, name: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Subject"
          value={newResult.subject}
          onChange={(e) => setNewResult({ ...newResult, subject: e.target.value })}
          required
        />
        <input
          type="number"
          placeholder="Score"
          value={newResult.score}
          onChange={(e) => setNewResult({ ...newResult, score: e.target.value })}
          required
        />
        <button type="submit">Add Result</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Subject</th>
            <th>Score</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {results.map(result => (
            <tr key={result.id}>
              <td>{result.id}</td>
              <td>{result.name}</td>
              <td>{result.subject}</td>
              <td>{result.score}</td>
              <td>
                <button onClick={() => handleDelete(result.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
